DROP TABLE IF EXISTS `sketch_settings`;
DROP TABLE IF EXISTS `sketch_views`;
DROP TABLE IF EXISTS `sketch_page`;
CREATE TABLE `sketch_settings` (
  `sketch_settings_id` int(10) NOT NULL AUTO_INCREMENT,
  `main_site_url` varchar(255) DEFAULT NULL,
  `theme_path` varchar(255) DEFAULT NULL,
  `cache_seconds` int(10) DEFAULT '2592000',
  `global_update` datetime DEFAULT NULL,
  PRIMARY KEY (`sketch_settings_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `sketch_page` (
  `page_id` int(10) NOT NULL AUTO_INCREMENT,
  `page_description` varchar(255) DEFAULT NULL,
  `page_keywords` varchar(255) DEFAULT NULL,
  `page_title` varchar(255) DEFAULT NULL,
  `content` mediumtext,
  `edit` mediumtext,
  `page_updated` datetime NOT NULL,
  `updated_by` varchar(80) DEFAULT NULL,
  `page_robots` varchar(255) DEFAULT 'INDEX,FOLLOW',
  `page_canonical` varchar(255) DEFAULT NULL,
  `rssfeed` varchar(255) DEFAULT NULL,
  `rsstext` varchar(255) DEFAULT NULL,
  `page_date` date DEFAULT NULL,
  `page_expiry` date DEFAULT NULL,
  `page_type` varchar(25) DEFAULT 'general',
  `page_status` varchar(25) DEFAULT 'published',
  `pagefile` varchar(50) DEFAULT NULL,
  `pageoutput` varchar(50) DEFAULT NULL,
  `pageform` varchar(50) DEFAULT NULL,
  `page_cache` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `page_heading` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`page_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `sketch_menu`;
CREATE TABLE `sketch_menu` (
  `sketch_menu_id` int(10) NOT NULL AUTO_INCREMENT,
  `menu_name` varchar(120) NOT NULL,
  `page_id` int(10) NOT NULL,
  `menu_mobile` int(10) NOT NULL DEFAULT '1',
  `menu_guid` varchar(255) DEFAULT NULL,
  `sketch_settings_id` int(10) NOT NULL,
  `menu_order` int(11) DEFAULT NULL,
  `menu_under` int(11) DEFAULT NULL,
  `menu_show` int(11) DEFAULT NULL,
  `menu_class` varchar(80) DEFAULT NULL,
  PRIMARY KEY (`sketch_menu_id`),
  UNIQUE KEY `Index__5` (`menu_guid`,`sketch_settings_id`),
  KEY `page__id` (`page_id`),
  KEY `FK__sketch_menu_3` (`menu_under`),
  KEY `Index__8` (`menu_under`),
  KEY `Index__9` (`menu_show`),
  CONSTRAINT `FK__sketch_menu_1` FOREIGN KEY (`page_id`) REFERENCES `sketch_page` (`page_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
DROP TABLE IF EXISTS `invoice`;
CREATE TABLE `invoice` (
  `invoice_id` int(11) NOT NULL AUTO_INCREMENT,
  `page_id` int(11) NOT NULL,
  `invoice_details` text,
  `emailed` int(11) DEFAULT NULL,
  `result` varchar(255) DEFAULT NULL,
  `amount` float DEFAULT NULL,
  `invoice_ref` varchar(255) DEFAULT NULL,
  `response_code` varchar(255) DEFAULT NULL,
  `invoice_response` varchar(255) DEFAULT NULL,
  `rrn` varchar(255) DEFAULT NULL,
  `invoice_date` datetime DEFAULT NULL,
  PRIMARY KEY (`invoice_id`),
  KEY `FK__invoice_1` (`page_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `panel`;
CREATE TABLE `panel` (
  `panel_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `panel_content` text COLLATE utf8_unicode_ci NOT NULL,
  `panel_heading` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `panel_image` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `panel_thumbnail` varchar(55) COLLATE utf8_unicode_ci NOT NULL,
  `panel_link` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `panel_type` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`panel_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;
DROP TABLE IF EXISTS `panel_to_page`;
CREATE TABLE `panel_to_page` (
  `panel_to_page_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `panel_id` int(10) unsigned NOT NULL,
  `page_id` int(10) unsigned NOT NULL,
  `panel_order` int(10) unsigned NOT NULL,
  PRIMARY KEY (`panel_to_page_id`),
  KEY `FK__panel_to_page_1` (`panel_id`),
  KEY `Index__3` (`page_id`),
  CONSTRAINT `FK__panel_to_page_1` FOREIGN KEY (`panel_id`) REFERENCES `panel` (`panel_id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `plugin`;
CREATE TABLE `plugin` (
  `plugin_id` int(11) NOT NULL AUTO_INCREMENT,
  `php` int(11) DEFAULT '1',
  `activated` int(11) NOT NULL DEFAULT '0',
  `name` varchar(50) NOT NULL,
  `menu_name` varchar(255) DEFAULT NULL,
  `is_admin` int(11) NOT NULL DEFAULT '0',
  `is_super_admin` int(11) DEFAULT '0',
  `global_location` varchar(25) DEFAULT NULL,
  `admin_order` int(11) DEFAULT NULL,
  `site_order` int(11) DEFAULT NULL,
  `content` longtext,
  `edit` longtext,
  PRIMARY KEY (`plugin_id`),
  UNIQUE KEY `plugin__name` (`name`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 PACK_KEYS=0 ROW_FORMAT=DYNAMIC;
CREATE TABLE `sketch_views` (
  `view_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `dateviewed` date DEFAULT NULL,
  `viewcount` int(11) DEFAULT '0',
  `page_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`view_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `tag`;
CREATE TABLE `tag` (
  `tag_id` int(11) NOT NULL AUTO_INCREMENT,
  `page_id` int(11) NOT NULL,
  `tag_content` text,
  `tag_name` varchar(50) DEFAULT NULL,
  `display_tag` int(11) DEFAULT NULL,
  PRIMARY KEY (`tag_id`),
  KEY `index__page_id` (`page_id`),
  CONSTRAINT `wwdb__tag_ibfk_1` FOREIGN KEY (`page_id`) REFERENCES `sketch_page` (`page_id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `template`;
CREATE TABLE `template` (
  `template_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `template_content` longtext,
  `template_type` varchar(150) DEFAULT 'page',
  `template_name` varchar(150) DEFAULT NULL,
  PRIMARY KEY (`template_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_login` varchar(50) DEFAULT NULL,
  `user_password` varchar(50) DEFAULT NULL,
  `user_email` varchar(150) DEFAULT NULL,
  `is_super` int(11) DEFAULT '0',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;